mapper.update:
update task
set
  taskState = #{taskState,jdbcType=VARCHAR},
  comments = #{comments,jdbcType=VARCHAR},
  star = #{star,jdbcType=INTEGER}
where taskId = #{taskId,jdbcType=VARCHAR}

mapper.select:
select taskId,content, taskState, description,taskDate,name,avatarUrl,address,task.openid1
from task,insured
where task.openid = insured.openid and taskState=#{state}

SELECT taskId FROM task
WHERE taskId LIKE #{current}
ORDER BY taskId DESC LIMIT 1

mapper.update:
  update task set
  taskState = #{taskState,jdbcType=VARCHAR},
  openid2 = #{openid2,jdbcType=VARCHAR}
where taskId = #{taskId,jdbcType=VARCHAR}
